from fastapi import APIRouter, Depends
from pydantic import BaseModel
from typing import List, Dict
from sqlalchemy.orm import Session

from app.core.db import get_db
from app.repositories.TrailsRepository import TrailsRepository

router = APIRouter(tags=["trails"])

# Pydantic schema de saída (não confundir com o ORM)
class TrailOut(BaseModel):
    id: int
    name: str
    thumbnail_url: str
    author: str | None = None
    review: int | None = None
    description: str | None = None

    class Config:
        from_attributes = True  # permite .from_orm / mapear atributos

@router.get("/showcase", response_model=Dict[str, List[TrailOut]])
def get_trails_showcase(db: Session = Depends(get_db)):
    repo = TrailsRepository(db)
    trails = repo.list_showcase()

    # mapear ORM → schema (ajustando snake_case → camelCase esperado)
    result = [
        TrailOut(
            id=t.id,
            name=t.name,
            thumbnail_url=t.thumbnail_url,  # <- campo do ORM é thumbnail_url
            author=t.author,
            review=t.review,
            description=t.description
        )
        for t in trails
    ]
    return {"trails": result}

@router.get("/", response_model=Dict[str, List[TrailOut]])
def get_trails(db: Session = Depends(get_db)):
    repo = TrailsRepository(db)
    trails = repo.list_all()

    # mapear ORM → schema (ajustando snake_case → camelCase esperado)
    result = [
        TrailOut(
            id=t.id,
            name=t.name,
            thumbnail_url=t.thumbnail_url,  
            author=t.author,
            review=t.review,
            description=t.description
        )
        for t in trails
    ]
    return {"trails": result}

